        string memory name,
        string memory symbol,
        address _pewCore
    ) public onlyPew returns (PewNFT) {
        PewNFT pewAddress = new PewNFT(name, symbol, _pewCore);

        collections[collectionsCounter] = pewAddress;
        collectionsCounter++;

        return pewAddress;
